Attribute.code:
 int counter;
sc_dt::sc_uint<12> inr;
sc_dt::sc_uint<12> inr1;
void comparator_main(){
	if (enable.read()==0){
		counter=0;
		inr1=0;
		compienable.write(0);
	}
	else{
		//printf("enabled\n");
		counter=counter+1;
		inr=(inputfromabs.read());
		if  (inr>inr1){
			inr1=inr;
		}
		else {
			inr1=inr1;
		}
		if(counter==30){
			outputmax.write(inr1);
			counter=0;
			inr1=0;
			compienable.write(1);
		}
		else {
			outputmax.write(0);
			compienable.write(0);
		}
	}
}

 sc_dt::sc_uint<12> inr1[30];
int i;
void spi_main() {
	int counter=0;
	while(in_ams.read()==1){
		if (compinenable.read()==1 ) {
			inr1[counter]=comparatorinput.read();
			counter=counter+1;
		}
		if(counter==30){
			for(i=0;i<30;i++) {
				out_ams.write(1);
			}
			counter=0;
		}
		         	}
}

