Attribute.processCode:
void initialize() {
	out.initialize(0);
}
     void processing() {
	NBitsTDF_type tmp_in = adc_in.read();
	out.write((int) tmp_in);
}

void processing() {
	double T_0_ = 1.0 / f_0_;
	double T_period_ = n_period_ * T_0_;
	double t = this->get_time().to_seconds();
	double t_pos = fmod(t, T_period_);
	int harmonic = static_cast<int>(floor(t / T_period_)) % (n_harmonic_ + 1);
	double val = offset_;
	val += amplitude_ * sin(2.0 * M_PI * pow(2.0, harmonic) * f_0_ * t_pos);
	out.write(val);
}

void processing() {
	if (!start_up_) {
		x_dot_ = (x_in.read() - x_in_last_) / x_in.get_timestep().to_seconds();
	}
	else {
		start_up_ = false;
	}
	x_in_last_ = x_in.read();
	v_out.write(k_ * x_dot_);
}

void processing() {
	double k = k_in.read();
	double val = std::pow(2.0, k) * in.read();
	if (val > v_supply_) {
		out.write(v_supply_);
	}
	else if (val < -v_supply_) {
		out.write(-v_supply_);
	}
	else {
		out.write(val);
	}
}

void processing() {
	double v_in = in.read();
	if (v_in < -v_max_) {
		out0.write(-((1 << (NBitsADC - 1)) - 1));
		out1.write(-((1 << (NBitsADC - 1)) - 1));
	}
	else if (v_in > v_max_) {
		out0.write((1 << (NBitsADC - 1)) - 1);
		out1.write((1 << (NBitsADC - 1)) - 1);
	}
	else {
		NBitsADC_type q_v_in = lround((v_in / v_max_) * ((1 << (NBitsADC - 1)) - 1));
		out0.write(q_v_in);
		out1.write(q_v_in);
	}
}

void initialize() {
	clk.initialize(true, 0);
	clk.initialize(false, 1);
	amp.initialize(0);
}
     void processing() {
	clk.write(true, 0);
	clk.write(false, 1);
	long sum = 0;
	for (long i = 0; i < 64; ++i) {
		sum += std::labs(in.read(i));
	}
	long avg = sum / 64;
	amp.write(avg);
}

Attribute.code:
void adapt_gain() {
	switch (state_) {
		case 0:      if (amp_in.read() < low_threshold_) {
			state_ = 1;
			++k_;
		}
		else if (amp_in.read() >= high_threshold_) {
			state_ = 2;
			--k_;
		}
		break;
		case 1:      if (amp_in.read() < high_threshold_) {
			++k_;
		}
		else {
			state_ = 2;
			--k_;
		}
		break;
		case 2:      if (amp_in.read() < high_threshold_) {
			state_ = 0;
		}
		else {
			--k_;
		}
		break;
		default:      SC_REPORT_ERROR("/vibration_sensor/tdf_gain_controller", "Unexpected state.");
	}
	if (k_ < k_min_) {
		k_ = k_min_;
	}
	if (k_ > k_max_) {
		k_ = k_max_;
	}
	k_out.write(k_);
}
